sysauto_script.script:
var gdt =  new GlideDateTime();
var nowDate = new GlideDateTime();
gdt.addDaysUTC(1);
gs.info("## Dates between " + nowDate + " and " + gdt);

var overdueSoonNITasks = new GlideRecord("x_58872_needit_needit_task");
overdueSoonNITasks.addQuery('due_date', '>=', nowDate);
overdueSoonNITasks.addQuery('due_date', '<', gdt);
//overdueSoonNITasks.addQuery('state', '!=', 3);
overdueSoonNITasks.query();

while(overdueSoonNITasks.next()){
	gs.eventQueue("x_58872_needit.overdueSoonNeedItTask", overdueSoonNITasks, overdueSoonNITasks.number, gs.getUserName());
	gs.info("x_58872_needit.overdueSoonNeedItTask" + " - " + overdueSoonNITasks + " - " + overdueSoonNITasks.number + " - " + gs.getUserName());
	gs.info("##Close to Overdue NeedIt Task record = " + overdueSoonNITasks.number);
}
